INSERT INTO `gdt_portfolio` VALUES (1,'Greystone Data Technology','Doing Great Things',1,'<div style=\"text-align: justify;\">\n<div style=\"text-align: center;\">Founded in 2002, Greystone offers products and services that make reverse logistics operations as simple and efficient as possible. With over 16 years of experience, and over 2000 employees globally (most of the employees are in Greystone Data Systems Vietnam - a wholly owned subsidiary of GDT Inc. USA (DBA Greystone Data Technology)). Greystone provides innovative technology solutions for the wireless mobile device industry. Our diverse products range from commercial grade diagnostic tools and software provisioning to Business To Consumer (B2C) and Business To Business (B2B) products which provide remote diagnostics and device data transfer tools in various commercial, retail and e-commerce environments. Greystone closely follows the wireless mobile device industry trends and develops robust, reliable and secure solutions that satisfy the constantly changing realm.</div>\n</div>\n\n<p style=\"text-align:center\">&nbsp;</p>','bg_intro_VN cloud.jpg','bg_intro_VN-cloud_Banner-Mobile.jpg','[{\"name\":\"company_img1_mobile.jpg\",\"label\":\"mobile\",\"link\":\"\",\"display\":0},{\"name\":\"company_img1_mobile.jpg\",\"label\":\"pc\",\"link\":\"\",\"display\":0}]',1,'1','2016-05-26 14:35:01','1','2018-09-13 11:16:32',0);
